import React, { useEffect, useState } from 'react';

function App() {
  const [texts, setTexts] = useState([]);
  const [loading, setLoading] = useState(true);

  const [filterSection, setFilterSection] = useState('');
  const [filteredTexts, setFilteredTexts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/texts')
      .then(res => res.json())
      .then(data => {
        setTexts(data);
        setFilteredTexts(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const parseMapString = (mapString) => {
    const cleanedString = mapString.trim().replace(/^\{|\}$/g, '');
    const regex = /(\w+)=((?:[^=,]|=(?!\w+=))+)/g;
    const obj = {};
    let match;

    while ((match = regex.exec(cleanedString)) !== null) {
      const key = match[1];
      const value = match[2].trim();
      obj[key] = value;
    }

    return obj;
  };

  const handleFilter = () => {
    if (!filterSection) {
      setFilteredTexts(texts);
      return;
    }

    const filtered = texts.filter(({ content }) => {
      const data = parseMapString(content);
      return data.seccion === filterSection;
    });

    setFilteredTexts(filtered);
  };

  const handleClearFilter = () => {
    setFilterSection('');
    setFilteredTexts(texts);
  };

  // *** New function to clear database ***
  const handleClearDatabase = () => {
    if (!window.confirm('Are you sure you want to clear the database? This action cannot be undone.')) return;

    fetch('http://localhost:3000/texts', { method: 'DELETE' })
      .then(res => {
        if (res.ok) {
          setTexts([]);
          setFilteredTexts([]);
          alert('Database cleared!');
        } else {
          alert('Failed to clear database');
        }
      })
      .catch(() => alert('Error clearing database'));
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Scanned Texts</h1>

      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Filter by Section"
          value={filterSection}
          onChange={(e) => setFilterSection(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <button onClick={handleFilter} style={{ marginRight: 8 }}>Filter</button>
        <button onClick={handleClearFilter} style={{ marginRight: 16 }}>Clear Filter</button>

        {/* Clear DB button */}
        <button onClick={handleClearDatabase} style={{ backgroundColor: 'red', color: 'white' }}>
          Clear Database
        </button>
      </div>

      {loading && <p>Loading...</p>}
      {!loading && filteredTexts.length === 0 && <p>No texts found.</p>}

      <ul>
        {filteredTexts.map(({ id, content, created_at }) => {
          const data = parseMapString(content);
          return (
            <li key={id} style={{ marginBottom: 20 }}>
              <p><b>Nombre:</b> {data.nombre || 'N/A'}</p>
              <p><b>Domicilio:</b> {data.domicilio || 'N/A'}</p>
              <p><b>Clave Elector:</b> {data.clave_elector || 'N/A'}</p>
              <p><b>CURP:</b> {data.curp || 'N/A'}</p>
              <p><b>Fecha de Nacimiento:</b> {data.fecha_nacimiento || 'N/A'}</p>
              <p><b>Sección:</b> {data.seccion || 'N/A'}</p>
              <p><b>Año de Registro:</b> {data.ano_registro || 'N/A'}</p>
              <p><b>Vigencia:</b> {data.vigencia || 'N/A'}</p>
              <small>{new Date(created_at).toLocaleString()}</small>
              <hr />
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default App;
